/**
 * Minimal shim for `http-proxy-agent`
 *
 * Purpose:
 * - Provide a tiny, synchronous class that can be imported by libraries (like ccxt)
 *   which dynamically import `http-proxy-agent` during bundling.
 * - This shim does not perform real proxying. It's intended only so bundlers and
 *   runtime code paths that check for the presence of the module succeed.
 *
 * Exports:
 * - default: ProxyAgent (class)
 * - HttpProxyAgent: ProxyAgent (named export)
 *
 * Compatibility:
 * - CommonJS consumers: require('http-proxy-agent') will return the ProxyAgent class (module.exports).
 * - ESM-aware bundlers that read `.default` will also find the class on `.default`.
 */
'use strict';
/**
 * Minimal ProxyAgent class.
 * Stores provided proxy url / options and exposes a small API surface.
 */
class ProxyAgent {
  /**
   * Construct a new ProxyAgent shim.
   * @param {string|object} proxy - Proxy url string or options object (kept for compatibility).
   * @param {object} [options] - Optional options (ignored by shim).
   */
  constructor(proxy, options = {}) {
    this.proxy = proxy;
    this.options = options;
    // Mark as shim for easier debugging if used at runtime.
    this.__isProxyAgentShim = true;
  }
  /**
   * Return a simple description of this proxy agent.
   * Useful if runtime code inspects the agent.
   * @returns {string}
   */
  toString() {
    try {
      if (typeof this.proxy === 'string') return `ProxyAgent(shim:${this.proxy})`;
      if (this.proxy && typeof this.proxy === 'object' && this.proxy.href) return `ProxyAgent(shim:${this.proxy.href})`;
      return 'ProxyAgent(shim)';
    } catch {
      return 'ProxyAgent(shim)';
    }
  }
  /**
   * Small helper that would normally return a Node Agent object.
   * In this shim it returns null. Present so code that expects a method does not crash.
   * @returns {null}
   */
  getAgent() {
    return null;
  }
}
// CommonJS exports to satisfy various import styles
module.exports = ProxyAgent;
module.exports.default = ProxyAgent;
module.exports.HttpProxyAgent = ProxyAgent;
exports.HttpProxyAgent = ProxyAgent;