/**
 * Minimal shim for `https-proxy-agent`
 *
 * Purpose:
 * - Provide a tiny, synchronous class that can be imported by libraries (like ccxt)
 *   which dynamically import `https-proxy-agent` during bundling.
 * - This shim does not perform real proxying. It's intended only so bundlers and
 *   runtime code paths that check for the presence of the module succeed.
 *
 * Exports:
 * - default: ProxyAgent (class)
 * - HttpsProxyAgent: ProxyAgent (named export)
 *
 * Compatibility:
 * - CommonJS consumers: require('https-proxy-agent') will return the ProxyAgent class (module.exports).
 * - ESM-aware bundlers that read `.default` will also find the class on `.default`.
 */
'use strict';
/**
 * Minimal ProxyAgent class for HTTPS proxies.
 */
class ProxyAgent {
  /**
   * Construct a new ProxyAgent shim.
   * @param {string|object} proxy - Proxy url string or options object (kept for compatibility).
   * @param {object} [options] - Optional options (ignored by shim).
   */
  constructor(proxy, options = {}) {
    this.proxy = proxy;
    this.options = options;
    this.__isHttpsProxyAgentShim = true;
  }
  /**
   * Return a readable description for debugging.
   * @returns {string}
   */
  toString() {
    try {
      if (typeof this.proxy === 'string') return `HttpsProxyAgent(shim:${this.proxy})`;
      if (this.proxy && typeof this.proxy === 'object' && this.proxy.href) return `HttpsProxyAgent(shim:${this.proxy.href})`;
      return 'HttpsProxyAgent(shim)';
    } catch {
      return 'HttpsProxyAgent(shim)';
    }
  }
  /**
   * Placeholder for returning an agent-like object.
   * @returns {null}
   */
  getAgent() {
    return null;
  }
}
// CommonJS exports to satisfy various import styles
module.exports = ProxyAgent;
module.exports.default = ProxyAgent;
module.exports.HttpsProxyAgent = ProxyAgent;
exports.HttpsProxyAgent = ProxyAgent;