/**
 * Minimal shim for `socks-proxy-agent`
 *
 * Purpose:
 * - Provide a tiny, synchronous class that can be imported by libraries (like ccxt)
 *   which dynamically import `socks-proxy-agent` during bundling/runtime.
 * - This shim does not perform real proxying. It's intended only so bundlers and
 *   runtime code paths that check for the presence of the module succeed.
 *
 * Exports:
 * - default: SocksProxyAgent (class)
 * - SocksProxyAgent: SocksProxyAgent (named export)
 *
 * Compatibility:
 * - CommonJS consumers: require('socks-proxy-agent') will return the SocksProxyAgent class (module.exports).
 * - ESM-aware bundlers that read `.default` will also find the class on `.default`.
 */
'use strict';
/**
 * Minimal SocksProxyAgent shim class.
 * Stores provided proxy url / options and exposes a small API surface.
 */
class SocksProxyAgent {
  /**
   * Construct a new SocksProxyAgent shim.
   * @param {string|object} proxy - Proxy url string or options object (kept for compatibility).
   * @param {object} [options] - Optional options (ignored by shim).
   */
  constructor(proxy, options = {}) {
    this.proxy = proxy;
    this.options = options;
    // Mark as shim for easier debugging if used at runtime.
    this.__isSocksProxyAgentShim = true;
  }
  /**
   * Return a simple description of this socks proxy agent.
   * Useful if runtime code inspects the agent.
   * @returns {string}
   */
  toString() {
    try {
      if (typeof this.proxy === 'string') return `SocksProxyAgent(shim:${this.proxy})`;
      if (this.proxy && typeof this.proxy === 'object' && this.proxy.href) return `SocksProxyAgent(shim:${this.proxy.href})`;
      return 'SocksProxyAgent(shim)';
    } catch {
      return 'SocksProxyAgent(shim)';
    }
  }
  /**
   * Placeholder that would normally return a Node.js Agent-like object.
   * In this shim it returns null. Present so code that expects a method does not crash.
   * @returns {null}
   */
  getAgent() {
    return null;
  }
}
// CommonJS exports to satisfy various import styles
module.exports = SocksProxyAgent;
module.exports.default = SocksProxyAgent;
module.exports.SocksProxyAgent = SocksProxyAgent;
exports.SocksProxyAgent = SocksProxyAgent;